import { NavLink } from "react-router";
import { AuthContext } from "../context";
import { useContext, useEffect } from "react";

const activate = true;
const styleActive = "active";

let link = [
    {
        href: "/about",
        label: "About",
    },
    {
        href: "/login",
        label: "Login"
    },
    {
        href: "/registration",
        label: "Registration"
    },
    {
        href: "/cabinet",
        label: "Cabinet"
    }
];

export default function MyLinks() {
    let {links, setLinks, isAuth} = useContext(AuthContext);

    useEffect(() => {
        if(isAuth) {
                setLinks(
                    {
                        href: "/about",
                        label: "About",
                    },
                    {
                        href: "/cabinet",
                        label: "Cabinet"
                    }
                )
                console.log(links);

        }
    })


    return (
        <ul>
        {link.map((item, i) => (
            <li key={i}>
                <NavLink
                    to={`${item.href}`}
                    className={({isActive}) => {
                        if(activate) 
                            return isActive ? styleActive : "";
                    }}>
                    {`${item.label}`}
                </NavLink>
            </li>
        ))}
        </ul>
    );
}
