import { NavLink } from "react-router";
import { AuthContext } from "../context";
import { useContext } from "react";

const activate = true;
const styleActive = "active";



export default function MyLinks() {
    let [links, setLinks] = useContext(AuthContext);
    setLinks(   {
        href: "/about",
        label: "About",
    },
    {
        href: "/login",
        label: "Login"
    },
    {
        href: "/registration",
        label: "Registration"
    },
    {
        href: "/cabinet",
        label: "Cabinet"
    });

    let link = [
        ...links
    ];

    return (
        <ul>
        {link.map((item, i) => (
            <li key={i}>
                <NavLink
                    to={`${item.href}`}
                    className={({isActive}) => {
                        if(activate) 
                            return isActive ? styleActive : "";
                    }}>
                    {`${item.label}`}
                </NavLink>
            </li>
        ))}
        </ul>
    );
}
